import React, { useState, useCallback } from 'react';
import Cropper from 'react-easy-crop';
import getCroppedImg from './cropImage'; // 引入輔助函數
import './App.css'; // 我稍後會建立這個檔案

const App = () => {
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [rotation, setRotation] = useState(0);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  const [imageToCrop, setImageToCrop] = useState(null); // 用於儲存使用者上傳的圖片或預設圖片
  const [croppedImage, setCroppedImage] = useState(null); // 新增狀態來儲存裁剪後的圖片 URL

  const onCropComplete = useCallback((croppedArea, croppedAreaPixelsValue) => {
    setCroppedAreaPixels(croppedAreaPixelsValue);
  }, []);

  const showCroppedImage = useCallback(async () => {
    if (!imageToCrop || !croppedAreaPixels) {
      console.error('Image source or cropped area not available.');
      alert('請先上傳圖片並完成裁剪。');
      return;
    }
    try {
      const croppedImgResult = await getCroppedImg(
        imageToCrop,
        croppedAreaPixels,
        rotation
      );
      console.log('cropped image:', croppedImgResult);
      setCroppedImage(croppedImgResult);

      // 考慮在這裡儲存最終的裁剪設定
      if (imageToCrop && croppedAreaPixels) {
        try {
            // 使用 imageToCrop 的一部分作為 key，避免太長
            // 注意：如果 imageToCrop 是非常長的 base64 字串，直接作為 key 可能不理想
            // 可以考慮對 imageToCrop 做 hash 或取前N個字元等
            const imageKey = imageToCrop.substring(0, 50); 
            localStorage.setItem(
              'cropSettings-' + imageKey,
              JSON.stringify({ crop, zoom, rotation, croppedAreaPixels })
            );
            console.log('Crop settings saved for:', imageKey);
        } catch (error) {
            console.error("Error saving to localStorage:", error);
            // localStorage 可能已滿或不支援
        }
      }

    } catch (e) {
      console.error('Error cropping image:', e);
      alert('圖片裁剪失敗，請檢查控制台錯誤。');
    }
  }, [imageToCrop, croppedAreaPixels, rotation]);

  const onFileChange = async (e) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.addEventListener('load', () => {
        const newImageSrc = reader.result;
        setImageToCrop(newImageSrc);
        setCroppedImage(null);
        
        // 嘗試載入已儲存的設定
        try {
            const imageKey = newImageSrc.substring(0, 50);
            const savedSettings = localStorage.getItem('cropSettings-' + imageKey);
            if (savedSettings) {
              const { crop: savedCrop, zoom: savedZoom, rotation: savedRotation, croppedAreaPixels: savedPixels } = JSON.parse(savedSettings);
              setCrop(savedCrop || { x: 0, y: 0 });
              setZoom(savedZoom || 1);
              setRotation(savedRotation || 0);
              setCroppedAreaPixels(savedPixels || null); // 也載入這個，以便直接顯示上次的結果
              console.log('Loaded saved settings for:', imageKey);
              // 如果也載入了 croppedAreaPixels，可以考慮直接觸發一次 showCroppedImage
              // showCroppedImage(); // 這樣會立即顯示裁剪結果，但需要確保 croppedAreaPixels 確實有效
            } else {
              // 沒有儲存的設定，則重設
              setCrop({ x: 0, y: 0 });
              setZoom(1);
              setRotation(0);
              setCroppedAreaPixels(null);
            }
        } catch (error) {
            console.error("Error loading from localStorage:", error);
             // 出錯則使用預設值
            setCrop({ x: 0, y: 0 });
            setZoom(1);
            setRotation(0);
            setCroppedAreaPixels(null);
        }
      });
      reader.readAsDataURL(file);
    }
  };

  // 手動儲存和載入按鈕 (可選)
  const handleSaveSettings = () => {
    if (imageToCrop && croppedAreaPixels) {
      try {
        const imageKey = imageToCrop.substring(0, 50);
        localStorage.setItem(
          'cropSettings-' + imageKey,
          JSON.stringify({ crop, zoom, rotation, croppedAreaPixels })
        );
        alert('目前裁剪設定已儲存!');
      } catch (error) {
        console.error("Error saving to localStorage:", error);
        alert('儲存設定失敗!');
      }
    } else {
      alert('沒有圖片或裁剪區域可儲存。');
    }
  };

  const handleLoadSettings = () => {
    if (imageToCrop) {
      try {
        const imageKey = imageToCrop.substring(0, 50);
        const savedSettings = localStorage.getItem('cropSettings-' + imageKey);
        if (savedSettings) {
          const { crop: savedCrop, zoom: savedZoom, rotation: savedRotation, croppedAreaPixels: savedPixels } = JSON.parse(savedSettings);
          setCrop(savedCrop || { x: 0, y: 0 });
          setZoom(savedZoom || 1);
          setRotation(savedRotation || 0);
          setCroppedAreaPixels(savedPixels || null);
          alert('已載入儲存的設定!');
          // 載入後可以選擇是否自動顯示裁剪結果
          if (savedPixels && imageToCrop) {
            //   showCroppedImage(); // 這會重新計算並顯示，如果 croppedImage 狀態也存取會更好
            //   可以直接用 savedPixels 去產生 croppedImage, 如果 getCroppedImg 支援
            //   或者，更簡單的方式是讓使用者點擊「顯示/更新裁剪結果」按鈕
          }
        } else {
          alert('找不到此圖片的儲存設定。');
        }
      } catch (error) {
        console.error("Error loading from localStorage:", error);
        alert('載入設定失敗!');
      }
    } else {
      alert('請先上傳圖片才能載入設定。');
    }
  };

  return (
    <div className="App">
      <div className="controls top-controls">
        <input type="file" accept="image/*" onChange={onFileChange} />
      </div>
      <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'flex-start', gap: '20px' }}>
        <div className="crop-container">
          {imageToCrop ? (
            <Cropper
              image={imageToCrop}
              crop={crop}
              zoom={zoom}
              rotation={rotation}
              aspect={1}
              cropShape="round"
              showGrid={false}
              onCropChange={setCrop}
              onZoomChange={setZoom}
              onRotationChange={setRotation}
              onCropComplete={onCropComplete}
            />
          ) : (
            <p>請上傳一張圖片進行裁剪。</p>
          )}
        </div>
        {/* 顯示裁剪後的圖片 */} 
        {croppedImage && (
          <div className="cropped-image-container">
            <h3>裁剪結果:</h3>
            <img src={croppedImage} alt="Cropped" style={{ maxWidth: '100%', maxHeight: '300px' }} />
            {/* 新增下載按鈕 */} 
            <a href={croppedImage} download="cropped-image.png" className="download-button">
              下載圖片
            </a>
          </div>
        )}
      </div>
      {imageToCrop && (
        <div className="controls bottom-controls">
          <div className="slider-container">
            <label htmlFor="zoom">縮放: {zoom.toFixed(2)}</label>
            <input
              id="zoom"
              type="range"
              value={zoom}
              min={1}
              max={3}
              step={0.1}
              aria-labelledby="Zoom"
              onChange={(e) => setZoom(Number(e.target.value))}
              className="slider"
            />
          </div>
          <div className="slider-container">
            <label htmlFor="rotation">旋轉: {rotation}°</label>
            <input
              id="rotation"
              type="range"
              value={rotation}
              min={0}
              max={360}
              step={1}
              aria-labelledby="Rotation"
              onChange={(e) => setRotation(Number(e.target.value))}
              className="slider"
            />
          </div>
          <button onClick={showCroppedImage} className="show-result-button">
            顯示/更新裁剪結果
          </button>
          {/* 新增手動儲存和載入按鈕 */}
          <button onClick={handleSaveSettings} className="control-button">
            儲存目前設定
          </button>
          <button onClick={handleLoadSettings} className="control-button">
            載入儲存設定
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
