import React, { useState, useCallback } from 'react';
import Cropper from 'react-easy-crop';
import './App.css'; // 我稍後會建立這個檔案

const App = () => {
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [rotation, setRotation] = useState(0);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  // 您需要提供一個圖片 URL。為了方便展示，我先用一個 placeholder 圖片。
  // 您可以替換成自己的圖片 URL 或是一個讓使用者上傳圖片的功能。
  const imageSrc = 'https://dummyimage.com/600x400/000/fff&text=Placeholder+Image';

  const onCropComplete = useCallback((croppedArea, croppedAreaPixelsValue) => {
    console.log('croppedArea:', croppedArea);
    console.log('croppedAreaPixels:', croppedAreaPixelsValue);
    setCroppedAreaPixels(croppedAreaPixelsValue);
  }, []);

  const showCroppedImage = useCallback(async () => {
    if (!imageSrc || !croppedAreaPixels) {
      console.error('Image source or cropped area not available.');
      return;
    }
    // 這裡您可以加入將 croppedAreaPixels 轉換成圖片並顯示或下載的邏輯
    // 例如使用一個 <canvas> 元素來繪製裁剪後的圖片
    console.log('Simulating showing cropped image with data:', croppedAreaPixels);
    alert('裁剪後的圖片資訊已在控制台輸出。請查看控制台以獲得裁剪區域的像素資訊。');
  }, [imageSrc, croppedAreaPixels]);

  return (
    <div className="App">
      <div className="crop-container">
        {imageSrc ? (
          <Cropper
            image={imageSrc}
            crop={crop}
            zoom={zoom}
            rotation={rotation}
            aspect={4 / 3}
            onCropChange={setCrop}
            onZoomChange={setZoom}
            onRotationChange={setRotation}
            onCropComplete={onCropComplete}
          />
        ) : (
          <p>Loading image...</p>
        )}
      </div>
      <div className="controls">
        <div className="slider-container">
          <label htmlFor="zoom">縮放: {zoom.toFixed(2)}</label>
          <input
            id="zoom"
            type="range"
            value={zoom}
            min={1}
            max={3}
            step={0.1}
            aria-labelledby="Zoom"
            onChange={(e) => setZoom(Number(e.target.value))}
            className="slider"
          />
        </div>
        <div className="slider-container">
          <label htmlFor="rotation">旋轉: {rotation}°</label>
          <input
            id="rotation"
            type="range"
            value={rotation}
            min={0}
            max={360}
            step={1}
            aria-labelledby="Rotation"
            onChange={(e) => setRotation(Number(e.target.value))}
            className="slider"
          />
        </div>
        <button onClick={showCroppedImage} className="show-result-button">
          顯示裁剪結果 (控制台)
        </button>
      </div>
    </div>
  );
};

export default App;
